import React, { useEffect, useState, useCallback } from 'react';
import Create from './Create';
import axios from 'axios';
import { BsCircleFill, BsFillCheckCircleFill, BsFillTrashFill, BsCalendarEvent } from 'react-icons/bs';
import { useAuth } from './AuthContext';
import './App.css';

const Home = () => {
    const [todos, setTodos] = useState([]);
    const [currentTime, setCurrentTime] = useState(new Date());
    const { token, logout } = useAuth();

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }, 1000);

        return () => clearInterval(timer);
    }, []);

    const fetchTodos = useCallback(() => {
        axios.get('http://localhost:5000/get', {
            headers: { 'x-auth-token': token }
        })
        .then(result => {
            // Sort todos by due date
            const sortedTodos = result.data.sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate));
            setTodos(sortedTodos);
        })
        .catch(err => {
            console.error(err);
            if (err.response?.status === 401) {
                logout();
            }
        });
    }, [token, logout]);

    useEffect(() => {
        fetchTodos();
    }, [fetchTodos]);

    const handleEdit = (id) => {
        axios.put(`http://localhost:5000/edit/${id}`, {}, {
            headers: { 'x-auth-token': token }
        })
        .then(() => {
            fetchTodos();
        })
        .catch(err => {
            console.error(err);
            if (err.response?.status === 401) {
                logout();
            }
        });
    };

    const handleDelete = (id) => {
        axios.delete(`http://localhost:5000/delete/${id}`, {
            headers: { 'x-auth-token': token }
        })
        .then(() => {
            fetchTodos();
        })
        .catch(err => {
            console.error(err);
            if (err.response?.status === 401) {
                logout();
            }
        });
    };

    const formatDate = (date) => {
        const dueDate = new Date(date);
        const today = new Date();
        const tomorrow = new Date(today);
        tomorrow.setDate(tomorrow.getDate() + 1);

        // Reset time part for comparison
        today.setHours(0, 0, 0, 0);
        tomorrow.setHours(0, 0, 0, 0);
        const dueDateNoTime = new Date(dueDate);
        dueDateNoTime.setHours(0, 0, 0, 0);

        if (dueDateNoTime.getTime() === today.getTime()) {
            return 'Today';
        } else if (dueDateNoTime.getTime() === tomorrow.getTime()) {
            return 'Tomorrow';
        } else {
            return dueDate.toLocaleDateString('en-US', { 
                month: 'short', 
                day: 'numeric',
                year: dueDate.getFullYear() !== today.getFullYear() ? 'numeric' : undefined
            });
        }
    };

    const getDueDateColor = (date) => {
        const dueDate = new Date(date);
        const today = new Date();
        const diffTime = dueDate - today;
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

        if (diffDays < 0) return 'overdue';
        if (diffDays === 0) return 'due-today';
        if (diffDays === 1) return 'due-tomorrow';
        if (diffDays <= 3) return 'due-soon';
        return 'due-later';
    };

    return (
        <div className="home">
            <div className="header">
                <h2>TASKBLOOM</h2>
                <div className="header-right">
                    <div className="time">{currentTime.toLocaleTimeString()}</div>
                    <button onClick={logout} className="logout-button">Logout</button>
                </div>
            </div>
            <Create />
            {todos.length === 0 ? (
                <div className="no-todos">No Tasks</div>
            ) : (
                todos.map(todo => (
                    <div className="task" key={todo._id}>
                        <div className="checkbox" onClick={() => handleEdit(todo._id)}>
                            {todo.done ?
                                <BsFillCheckCircleFill className="icon" />
                                : <BsCircleFill className="icon" />
                            }
                            <p className={todo.done ? "line_through" : ""}>{todo.task}</p>
                        </div>
                        <div className="task-right">
                            <div className={`due-date ${getDueDateColor(todo.dueDate)}`}>
                                <BsCalendarEvent className="calendar-icon" />
                                <span>{formatDate(todo.dueDate)}</span>
                            </div>
                            <BsFillTrashFill
                                className="icon"
                                onClick={() => handleDelete(todo._id)}
                            />
                        </div>
                    </div>
                ))
            )}
        </div>
    );
};

export default Home;
